import { useNavigate } from "react-router-dom";
import './Products.css'; // Import CSS for styling
import { FC } from "react";

export const Products: FC = () => {
  const productList = [
    { id: 1, name: "Product 1", description: "High-quality product 1", image: "https://via.placeholder.com/150" },
    { id: 2, name: "Product 2", description: "Innovative product 2", image: "https://via.placeholder.com/150" },
    { id: 3, name: "Product 3", description: "Top-notch product 3", image: "https://via.placeholder.com/150" },
  ];

  const navigate = useNavigate();

  const handleProductClick = (id: number) => {
    navigate(`/product/${id}`);
  };

  return (
    <div className="products-page">
      <h1>Our Products</h1>
      <div className="product-grid">
        {productList.map((product) => (
          <div key={product.id} className="product-card">
            <img src={product.image} alt={product.name} />
            <h2>{product.name}</h2>
            <p>{product.description}</p>
            <button onClick={() => handleProductClick(product.id)}>
              View Details
            </button>
          </div>
        ))}
      </div>
    </div>
  );
}
